import React, { useEffect, useState } from "react";

/**
 * Live latency display for Synaptra Core v3.0
 * -----------------------------------------------------
 * - Starts at given value (default 0)
 * - Smoothly animates updates
 * - Pulses on change
 * - Dark glass background with gradient text
 */
export default function LatencyCard({ latency = 0 }) {
  const [display, setDisplay] = useState(latency);
  const [pulse, setPulse] = useState(false);

  // Smooth animation + pulse feedback when latency changes
  useEffect(() => {
    setPulse(true);
    const step = (latency - display) / 10;
    const id = setInterval(() => {
      setDisplay((prev) => {
        const next = prev + step;
        if (Math.abs(next - latency) < 1) {
          clearInterval(id);
          return latency;
        }
        return next;
      });
    }, 30);
    const timeout = setTimeout(() => setPulse(false), 400);
    return () => {
      clearInterval(id);
      clearTimeout(timeout);
    };
  }, [latency]);

  return (
    <div
      className={`latency-card ${pulse ? "pulse" : ""}`}
      style={{
        background: "rgba(255,255,255,0.06)",
        border: "1px solid rgba(255,255,255,0.15)",
        borderRadius: "16px",
        padding: "20px 40px",
        textAlign: "center",
        backdropFilter: "blur(8px)",
        boxShadow: "0 0 25px rgba(94,231,255,0.12)",
        transition: "transform 0.25s ease, box-shadow 0.3s ease",
      }}
    >
      <div
        style={{
          fontSize: "13px",
          color: "rgba(255,255,255,0.6)",
          marginBottom: "6px",
          letterSpacing: "0.3px",
        }}
      >
        Live Latency
      </div>
      <div
        style={{
          fontSize: "26px",
          fontWeight: "800",
          background: "linear-gradient(90deg,#5ee7ff,#9e6cff)",
          WebkitBackgroundClip: "text",
          color: "transparent",
        }}
      >
        {Math.round(display)} ms
      </div>
    </div>
  );
}
