import React, { useState } from "react";
import Navbar from "./components/Navbar";
import FileUpload from "./components/FileUpload";
import LatencyCard from "./components/LatencyCard";
import SessionHistory from "./components/SessionHistory";
import Footer from "./components/Footer";

/**
 * Synaptra Core v3.0 Frontend
 * ----------------------------------------------------------
 * - Minimal dark glass UI with central hero section
 * - Uploads, latency display, and session log
 * - Fully responsive layout
 */
export default function App() {
  const [latency, setLatency] = useState(0);
  const [history, setHistory] = useState([]);

  // Simulated backend latency (for now)
  const simulateLatency = () => {
    const random = Math.floor(Math.random() * 150) + 50;
    setLatency(random);
    return random;
  };

  // Handle upload action
  const handleFile = (file) => {
    const kind = file.type.startsWith("image/")
      ? "image"
      : file.type.startsWith("audio/")
      ? "audio"
      : "file";
    simulateLatency();
    setHistory((prev) => [
      { kind, content: `Uploaded ${file.name}` },
      ...prev,
    ]);
  };

  // Handle text command
  const handleTextCommand = (e) => {
    e.preventDefault();
    const prompt = e.target.prompt.value.trim();
    if (!prompt) return;
    simulateLatency();
    setHistory((prev) => [{ kind: "text", content: prompt }, ...prev]);
    e.target.reset();
  };

  return (
    <div
      className="app"
      style={{
        background: "radial-gradient(circle at top,#0b0f1a 0%,#05070c 100%)",
        minHeight: "100vh",
        color: "white",
        fontFamily:
          "'Inter Variable', ui-sans-serif, system-ui, -apple-system, Roboto",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <Navbar />

      {/* Hero Section */}
      <header
        style={{
          marginTop: "100px",
          textAlign: "center",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: "20px",
          maxWidth: "700px",
        }}
      >
        <h1
          style={{
            fontSize: "42px",
            fontWeight: 800,
            background: "linear-gradient(90deg,#5ee7ff,#7c3aed)",
            WebkitBackgroundClip: "text",
            color: "transparent",
            letterSpacing: "-0.8px",
          }}
        >
          Synaptra Core v3.0
        </h1>
        <p
          style={{
            color: "rgba(255,255,255,0.65)",
            fontSize: "16px",
            lineHeight: "1.6",
          }}
        >
          Multimodal Intelligence Interface â€” process text, image, and audio
          through one unified agent pipeline.
        </p>
      </header>

      {/* Upload + Latency Row */}
      <section
        style={{
          display: "flex",
          flexWrap: "wrap",
          gap: "24px",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "60px",
        }}
      >
        <FileUpload label="Upload Image or Audio" onSelect={handleFile} />
        <LatencyCard latency={latency} />
      </section>

      {/* Text Command Input */}
      <section
        style={{
          marginTop: "50px",
          width: "100%",
          display: "flex",
          justifyContent: "center",
        }}
      >
        <form
          onSubmit={handleTextCommand}
          style={{
            background: "rgba(255,255,255,0.05)",
            borderRadius: "16px",
            border: "1px solid rgba(255,255,255,0.1)",
            padding: "12px 16px",
            width: "80%",
            maxWidth: "640px",
            display: "flex",
            gap: "10px",
            backdropFilter: "blur(10px)",
          }}
        >
          <input
            name="prompt"
            placeholder="Enter a text command (e.g., 'run print(2+2)')"
            style={{
              flex: 1,
              background: "transparent",
              color: "white",
              border: "none",
              outline: "none",
              fontSize: "15px",
            }}
          />
          <button
            type="submit"
            style={{
              background: "linear-gradient(90deg,#5ee7ff,#7c3aed)",
              border: "none",
              borderRadius: "10px",
              color: "white",
              fontWeight: 600,
              fontSize: "15px",
              padding: "8px 18px",
              cursor: "pointer",
              transition: "opacity 0.25s ease",
            }}
          >
            Send
          </button>
        </form>
      </section>

      {/* Session History */}
      <SessionHistory items={history} />

      <Footer />
    </div>
  );
}
