import React from "react";

/**
 * Session history display
 * ------------------------
 * - Shows recent text, image, and audio events
 * - Glassy container with clear hierarchy
 */
export default function SessionHistory({ items = [] }) {
  return (
    <section
      className="session-history"
      style={{
        marginTop: "40px",
        display: "flex",
        justifyContent: "center",
        width: "100%",
      }}
    >
      <div
        className="history-card"
        style={{
          background: "rgba(255,255,255,0.05)",
          borderRadius: "16px",
          padding: "24px",
          border: "1px solid rgba(255,255,255,0.1)",
          backdropFilter: "blur(10px)",
          boxShadow: "0 15px 40px rgba(0,0,0,0.25)",
          width: "80%",
          maxWidth: "720px",
        }}
      >
        <h3
          style={{
            color: "#5ee7ff",
            fontSize: "20px",
            fontWeight: 700,
            marginBottom: "18px",
          }}
        >
          Session History
        </h3>

        {items.length === 0 ? (
          <p style={{ color: "rgba(255,255,255,0.6)" }}>
            No session data yet. Upload something or send a text command.
          </p>
        ) : (
          items.map((item, i) => (
            <div
              key={i}
              style={{
                marginBottom: "12px",
                background: "rgba(255,255,255,0.03)",
                borderRadius: "10px",
                padding: "10px 14px",
              }}
            >
              <div
                style={{
                  color: "#7c3aed",
                  fontWeight: "600",
                  fontSize: "13px",
                  marginBottom: "4px",
                }}
              >
                {item.kind.toUpperCase()}
              </div>
              <div
                style={{
                  color: "rgba(255,255,255,0.85)",
                  fontSize: "15px",
                  lineHeight: "1.5",
                }}
              >
                {item.content}
              </div>
            </div>
          ))
        )}
      </div>
    </section>
  );
}
