import React, { useRef, useState } from "react";

/**
 * Upload component for Synaptra Core v3.0
 * ----------------------------------------
 * - Handles image/audio uploads with smooth hover effects
 * - Shows selected filename
 * - Calls parent callback with the selected file
 */
export default function FileUpload({ label = "Upload Image / Audio", onSelect }) {
  const inputRef = useRef(null);
  const [fileName, setFileName] = useState("");

  const handleChange = (e) => {
    const file = e.target.files?.[0];
    if (file) {
      setFileName(file.name);
      onSelect(file);
    }
    e.target.value = null;
  };

  return (
    <div
      className="file-upload"
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        background: "rgba(255,255,255,0.05)",
        borderRadius: "18px",
        padding: "20px",
        backdropFilter: "blur(8px)",
        border: "1px solid rgba(255,255,255,0.1)",
        boxShadow: "0 10px 30px rgba(0,0,0,0.25)",
        transition: "transform 0.2s ease, box-shadow 0.3s ease",
        width: "280px",
      }}
      onClick={() => inputRef.current?.click()}
      onMouseEnter={(e) => (e.currentTarget.style.transform = "scale(1.03)")}
      onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1.0)")}
    >
      <input
        type="file"
        accept="image/*,audio/*"
        ref={inputRef}
        hidden
        onChange={handleChange}
      />
      <button
        type="button"
        className="upload-btn"
        style={{
          fontWeight: 600,
          background: "linear-gradient(90deg, #5ee7ff, #7c3aed)",
          color: "white",
          border: "none",
          borderRadius: "12px",
          padding: "10px 16px",
          cursor: "pointer",
          fontSize: "15px",
          transition: "opacity 0.25s ease",
        }}
      >
        {label}
      </button>
      {fileName && (
        <span
          style={{
            fontSize: "13px",
            color: "rgba(255,255,255,0.7)",
            marginTop: "10px",
            wordBreak: "break-all",
          }}
        >
          {fileName}
        </span>
      )}
    </div>
  );
}
